import React, { useState } from 'react';

const ChatPage = ({ copies, chatHistory, setChatHistory, isDevMode }) => {
  const [chatMessage, setChatMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  const handleChat = async () => {
    if (!chatMessage.trim()) return;

    if (copies.length === 0) {
      const newHistory = [...chatHistory, { role: 'user', content: chatMessage }];
      setChatHistory([...newHistory, { 
        role: 'assistant', 
        content: 'Please copy some text first so I can help answer questions about it.',
        timestamp: new Date().toLocaleTimeString()
      }]);
      setChatMessage('');
      return;
    }

    const newHistory = [...chatHistory, { 
      role: 'user', 
      content: chatMessage,
      timestamp: new Date().toLocaleTimeString()
    }];
    setChatHistory(newHistory);
    setChatMessage('');
    setIsTyping(true);

    try {
      const response = await fetch('http://localhost:3001/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: chatMessage, copies })
      });
      const data = await response.json();
      
      // Simulate typing delay for better UX
      setTimeout(() => {
        setChatHistory([...newHistory, { 
          role: 'assistant', 
          content: data.response,
          timestamp: new Date().toLocaleTimeString()
        }]);
        setIsTyping(false);
      }, 1000);
    } catch (error) {
      console.error('Chat error:', error);
      setTimeout(() => {
        setChatHistory([...newHistory, { 
          role: 'assistant', 
          content: 'Unable to connect to chat service. Please check your connection.',
          timestamp: new Date().toLocaleTimeString()
        }]);
        setIsTyping(false);
      }, 500);
    }
  };

  const clearHistory = () => {
    setChatHistory([]);
  };

  return (
    <div className="h-full flex flex-col animate-[fadeInUp_0.3s_ease-out]">
      <div className="p-4 flex-1 flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 bg-blue-400 rounded-full animate-pulse"></div>
            <h2 className="text-sm font-medium text-gray-300">AI Assistant</h2>
            <span className="text-xs text-gray-500 bg-gray-800 px-2 py-1 rounded-full">
              {copies.length} items
            </span>
          </div>
          {chatHistory.length > 0 && (
            <button 
              onClick={clearHistory}
              className="text-xs text-gray-400 hover:text-red-400 transition-colors hover:scale-105 transform"
            >
              Clear History
            </button>
          )}
        </div>

        {/* Chat History */}
        <div className="flex-1 bg-gradient-to-br from-gray-900/80 to-gray-800/80 backdrop-blur-sm rounded-lg border border-gray-700/50 p-4 mb-4 overflow-y-auto scroll-smooth">
          {chatHistory.length === 0 ? (
            <div className="text-center text-gray-500 py-8 animate-[fadeIn_0.5s_ease-out]">
              <div className="w-16 h-16 bg-gradient-to-br from-blue-500/20 to-purple-500/20 rounded-2xl mx-auto mb-4 flex items-center justify-center animate-[float_3s_ease-in-out_infinite]">
                <span className="text-2xl">🤖</span>
              </div>
              <p className="text-sm font-medium mb-1">AI Assistant Ready</p>
              <p className="text-xs text-gray-600">Ask questions about your copied items</p>
              <div className="mt-4 text-xs text-gray-600">
                <div className="flex items-center justify-center gap-2 mb-2">
                  <span className="w-1 h-1 bg-gray-600 rounded-full"></span>
                  <span>Summarize text</span>
                </div>
                <div className="flex items-center justify-center gap-2 mb-2">
                  <span className="w-1 h-1 bg-gray-600 rounded-full"></span>
                  <span>Extract information</span>
                </div>
                <div className="flex items-center justify-center gap-2">
                  <span className="w-1 h-1 bg-gray-600 rounded-full"></span>
                  <span>Answer questions</span>
                </div>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              {chatHistory.map((msg, index) => (
                <div 
                  key={index} 
                  className={`animate-[slideInMessage_0.4s_ease-out] ${
                    msg.role === 'user' 
                      ? 'ml-6 text-right' 
                      : 'mr-6'
                  }`}
                  style={{ animationDelay: `${index * 0.1}s` }}
                >
                  <div className={`inline-block p-3 rounded-xl text-sm max-w-[85%] break-words relative ${
                    msg.role === 'user'
                      ? 'bg-gradient-to-r from-blue-600 to-blue-500 text-white shadow-lg'
                      : 'bg-gradient-to-r from-gray-800 to-gray-700 border border-gray-600/50 text-gray-200 shadow-lg'
                  }`}>
                    {msg.content}
                    <div className={`absolute w-3 h-3 ${
                      msg.role === 'user'
                        ? 'bg-gradient-to-r from-blue-600 to-blue-500 -bottom-1 -right-1 rotate-45'
                        : 'bg-gradient-to-r from-gray-800 to-gray-700 -bottom-1 -left-1 rotate-45'
                    }`}></div>
                  </div>
                  <div className={`text-xs text-gray-500 mt-2 ${
                    msg.role === 'user' ? 'text-right' : 'text-left'
                  }`}>
                    <span className="bg-gray-800/50 px-2 py-1 rounded-full">
                      {msg.role === 'user' ? 'You' : 'Assistant'} • {msg.timestamp}
                    </span>
                  </div>
                </div>
              ))}
              
              {/* Typing indicator */}
              {isTyping && (
                <div className="mr-6 animate-[slideInMessage_0.4s_ease-out]">
                  <div className="inline-block p-3 rounded-xl text-sm bg-gradient-to-r from-gray-800 to-gray-700 border border-gray-600/50 text-gray-200">
                    <div className="flex items-center gap-1">
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Chat Input */}
        <div className="flex gap-3 bg-gray-900/50 p-3 rounded-lg border border-gray-700/50 backdrop-blur-sm">
          <div className="flex-1 relative">
            <input
              type="text"
              value={chatMessage}
              onChange={(e) => setChatMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleChat()}
              placeholder="Ask about your clipboard content..."
              disabled={isTyping}
              className="w-full p-3 bg-black/50 border border-gray-600 rounded-lg text-sm text-white placeholder-gray-500 focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all disabled:opacity-50"
            />
            {chatMessage && (
              <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                <span className="text-xs text-gray-400">{chatMessage.length}</span>
              </div>
            )}
          </div>
          <button 
            onClick={handleChat} 
            disabled={!chatMessage.trim() || isTyping}
            className="px-6 py-3 bg-gradient-to-r from-blue-600 to-blue-500 hover:from-blue-500 hover:to-blue-400 disabled:from-gray-700 disabled:to-gray-600 disabled:cursor-not-allowed text-white rounded-lg text-sm font-medium transition-all transform hover:scale-105 disabled:scale-100 shadow-lg"
          >
            {isTyping ? (
              <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
            ) : (
              'Send'
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
