/* global chrome */
import React, { useState, useEffect } from 'react';

function App() {
  const [copies, setCopies] = useState([]);
  const [chatMessage, setChatMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [newCopyText, setNewCopyText] = useState('');
  const [isDevMode] = useState(typeof chrome === 'undefined' || !chrome.storage);

  useEffect(() => {
    // Load copies from storage
    if (typeof chrome !== 'undefined' && chrome.storage && chrome.storage.local) {
      chrome.storage.local.get(['copies'], (result) => {
        if (result.copies) {
          setCopies(result.copies);
        }
      });
    } else {
      // Fallback for development - use localStorage or mock data
      console.log('Chrome API not available - running in development mode');
      const devCopies = localStorage.getItem('devCopies');
      if (devCopies) {
        setCopies(JSON.parse(devCopies));
      } else {
        // Add some sample data for development
        const sampleCopies = [
          'Sample copied text 1',
          'Another piece of copied content',
          'Development test copy'
        ];
        setCopies(sampleCopies);
        localStorage.setItem('devCopies', JSON.stringify(sampleCopies));
      }
    }
  }, []);

  const handleAddCopy = () => {
    if (!newCopyText.trim()) return;

    const newCopies = [newCopyText, ...copies].slice(0, 10); // Keep only last 10
    setCopies(newCopies);
    setNewCopyText('');

    if (isDevMode) {
      localStorage.setItem('devCopies', JSON.stringify(newCopies));
    }
  };

  const handleChat = async () => {
    if (!chatMessage.trim()) return;

    // Check if there are any copies to chat about
    if (copies.length === 0) {
      const newHistory = [...chatHistory, { role: 'user', content: chatMessage }];
      setChatHistory([...newHistory, { role: 'assistant', content: 'Please copy some text first so I can help answer questions about it.' }]);
      setChatMessage('');
      return;
    }

    const newHistory = [...chatHistory, { role: 'user', content: chatMessage }];
    setChatHistory(newHistory);
    setChatMessage('');

    try {
      const response = await fetch('http://localhost:3001/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: chatMessage, copies })
      });
      const data = await response.json();
      setChatHistory([...newHistory, { role: 'assistant', content: data.response }]);
    } catch (error) {
      console.error('Chat error:', error);
      setChatHistory([...newHistory, { role: 'assistant', content: 'Sorry, I couldn\'t connect to the chat service. Please try again.' }]);
    }
  };

  return (
    <div className="w-[400px] h-[600px] bg-gradient-to-br from-gray-900 via-black to-gray-800 text-white overflow-hidden animate-[slideInFromRight_0.5s_ease-out] relative">
      {/* Background pattern */}
      <div className="absolute inset-0 bg-gradient-to-r from-blue-500/5 to-purple-500/5"></div>
      
      {/* Header */}
      <div className="relative z-10 p-6 border-b border-gray-700/50">
        <div className="flex items-center gap-3 mb-2">
          <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
            <span className="text-white font-bold text-sm">CC</span>
          </div>
          <h1 className="text-xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
            CopyCache
          </h1>
        </div>
        <p className="text-gray-400 text-xs">Manage your clipboard with AI assistance</p>
      </div>

      {/* Content Container */}
      <div className="relative z-10 flex flex-col h-[calc(100%-120px)]">
        
        {/* Copied Items Section */}
        <div className="p-4 flex-shrink-0">
          <div className="flex items-center gap-2 mb-3">
            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
            <h2 className="text-sm font-semibold text-gray-300">Recent Copies ({copies.length})</h2>
          </div>
          
          <div className="max-h-32 overflow-y-auto space-y-2 scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-transparent">
            {copies.length === 0 ? (
              <div className="text-center py-4 text-gray-500 text-xs">
                <div className="w-12 h-12 bg-gray-800 rounded-full mx-auto mb-2 flex items-center justify-center">
                  ðŸ“‹
                </div>
                No copies yet. Start copying text!
              </div>
            ) : (
              copies.map((copy, index) => (
                <div 
                  key={index} 
                  className="group bg-gray-800/60 hover:bg-gray-700/60 p-3 rounded-lg border border-gray-700/30 transition-all duration-200 hover:scale-[1.02] hover:shadow-lg cursor-pointer backdrop-blur-sm"
                >
                  <div className="text-xs text-gray-300 truncate" title={copy}>
                    {copy}
                  </div>
                  <div className="text-[10px] text-gray-500 mt-1">
                    Copy #{copies.length - index}
                  </div>
                </div>
              ))
            )}
          </div>

          {isDevMode && (
            <div className="mt-3 p-3 bg-yellow-900/20 border border-yellow-600/30 rounded-lg">
              <div className="text-[10px] text-yellow-400 mb-2">ðŸš§ Development Mode</div>
              <input
                type="text"
                value={newCopyText}
                onChange={(e) => setNewCopyText(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleAddCopy()}
                placeholder="Add test copy"
                className="w-full p-2 bg-gray-800 text-white border border-gray-600 rounded text-xs focus:border-blue-500 transition-colors"
              />
              <button 
                onClick={handleAddCopy} 
                className="mt-2 px-3 py-1 bg-blue-600 hover:bg-blue-500 text-white rounded text-xs transition-colors"
              >
                Add Copy
              </button>
            </div>
          )}
        </div>

        {/* Chat Section */}
        <div className="flex-1 p-4 flex flex-col">
          <div className="flex items-center gap-2 mb-3">
            <div className="w-2 h-2 bg-blue-400 rounded-full animate-pulse"></div>
            <h2 className="text-sm font-semibold text-gray-300">AI Assistant</h2>
          </div>

          {/* Chat History */}
          <div className="flex-1 bg-gray-900/60 backdrop-blur-sm rounded-lg border border-gray-700/30 p-3 mb-3 overflow-y-auto scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-transparent">
            {chatHistory.length === 0 ? (
              <div className="text-center text-gray-500 text-xs py-4">
                <div className="w-10 h-10 bg-gray-800 rounded-full mx-auto mb-2 flex items-center justify-center">
                  ðŸ¤–
                </div>
                Ask me anything about your copied text!
              </div>
            ) : (
              <div className="space-y-2">
                {chatHistory.map((msg, index) => (
                  <div 
                    key={index} 
                    className={`animate-[fadeInUp_0.3s_ease-out] ${
                      msg.role === 'user' 
                        ? 'ml-6 text-right' 
                        : 'mr-6'
                    }`}
                  >
                    <div className={`inline-block p-2 rounded-lg text-xs max-w-[80%] ${
                      msg.role === 'user'
                        ? 'bg-blue-600 text-white rounded-br-sm'
                        : 'bg-gray-700 text-gray-200 rounded-bl-sm'
                    }`}>
                      {msg.content}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Chat Input */}
          <div className="flex gap-2">
            <input
              type="text"
              value={chatMessage}
              onChange={(e) => setChatMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleChat()}
              placeholder="Ask about your copied text..."
              className="flex-1 p-2.5 bg-gray-800 text-white border border-gray-600 rounded-lg text-xs focus:border-blue-500 focus:outline-none transition-colors placeholder-gray-400"
            />
            <button 
              onClick={handleChat} 
              disabled={!chatMessage.trim()}
              className="px-4 py-2.5 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-500 hover:to-purple-500 disabled:from-gray-600 disabled:to-gray-600 text-white rounded-lg text-xs font-medium transition-all duration-200 hover:scale-105 disabled:scale-100 disabled:cursor-not-allowed"
            >
              Send
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
