/* global chrome */
import React, { useState, useEffect } from 'react';
import ClipboardPage from './pages/clipboard';
import ChatPage from './pages/chat';

function App() {
  const [copies, setCopies] = useState([]);
  const [chatHistory, setChatHistory] = useState([]);
  const [isDevMode] = useState(typeof chrome === 'undefined' || !chrome.storage);
  const [typewriterText, setTypewriterText] = useState('');
  const [isTyping, setIsTyping] = useState(true);
  const [activeTab, setActiveTab] = useState('clipboard');
  const [isLoading, setIsLoading] = useState(true);
  
  const fullText = "CopyCache";

  // Typewriter effect for logo
  useEffect(() => {
    if (isTyping && typewriterText.length < fullText.length) {
      const timeout = setTimeout(() => {
        setTypewriterText(fullText.slice(0, typewriterText.length + 1));
      }, 120);
      return () => clearTimeout(timeout);
    } else if (typewriterText.length === fullText.length) {
      const timeout = setTimeout(() => setIsTyping(false), 500);
      return () => clearTimeout(timeout);
    }
  }, [typewriterText, isTyping, fullText]);

  // Load copies from storage
  useEffect(() => {
    const loadData = async () => {
      setIsLoading(true);
      
      if (typeof chrome !== 'undefined' && chrome.storage && chrome.storage.local) {
        chrome.storage.local.get(['copies'], (result) => {
          if (result.copies) {
            setCopies(result.copies);
          }
          setIsLoading(false);
        });

        // Listen for new copies in real-time
        chrome.storage.onChanged.addListener((changes, namespace) => {
          if (namespace === 'local' && changes.copies) {
            setCopies(changes.copies.newValue || []);
          }
        });
      } else {
        // Development mode fallback
        const devCopies = localStorage.getItem('devCopies');
        if (devCopies) {
          setCopies(JSON.parse(devCopies));
        } else {
          // Add some sample data for development
          const sampleCopies = [
            'Welcome to CopyCache! This is a sample copied text to help you get started.',
            'Another example of copied content that you might have in your clipboard.',
            'Short text example',
            'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
            'https://example.com/some-long-url-that-you-copied',
            'Code snippet: const handleClick = () => { console.log("Hello World"); };'
          ];
          setCopies(sampleCopies);
          localStorage.setItem('devCopies', JSON.stringify(sampleCopies));
        }
        setIsLoading(false);
      }
    };

    loadData();
  }, []);

  const handleTabChange = (newTab) => {
    if (newTab !== activeTab) {
      setActiveTab(newTab);
    }
  };

  if (isLoading) {
    return (
      <div className="w-[400px] h-[600px] bg-black text-white flex items-center justify-center">
        <div className="text-center animate-[fadeIn_0.5s_ease-out]">
          <div className="w-12 h-12 border-4 border-gray-700 border-t-blue-500 rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-sm text-gray-400">Loading CopyCache...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="w-[400px] h-[600px] bg-gradient-to-br from-gray-900 via-black to-gray-800 text-white overflow-hidden relative animate-[slideInFromRight_0.5s_ease-out]">
      {/* Background Effects */}
      <div className="absolute inset-0 bg-gradient-to-r from-blue-500/5 to-purple-500/5"></div>
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,rgba(59,130,246,0.1)_0%,transparent_50%)]"></div>
      
      {/* Header */}
      <div className="relative z-10 bg-gray-900/80 backdrop-blur-sm border-b border-gray-700/50 p-4">
        <div className="flex items-center gap-3 mb-2">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center shadow-lg">
            <span className="text-white font-bold text-sm">CC</span>
          </div>
          <div>
            <h1 className="text-xl font-semibold text-white">
              <span className="font-mono bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
                {typewriterText}
                {isTyping && <span className="animate-pulse text-blue-400">|</span>}
              </span>
            </h1>
            <p className="text-gray-400 text-xs">Smart Clipboard Manager</p>
          </div>
        </div>
        {isDevMode && (
          <div className="bg-yellow-900/20 border border-yellow-600/30 rounded-lg px-3 py-1">
            <p className="text-yellow-400 text-xs font-medium">🚧 Development Mode</p>
          </div>
        )}
      </div>

      {/* Navigation Tabs */}
      <div className="relative z-10 bg-gray-800/80 backdrop-blur-sm border-b border-gray-700/50">
        <div className="flex relative">
          <button
            onClick={() => handleTabChange('clipboard')}
            className={`flex-1 px-4 py-3 text-sm font-medium transition-all duration-300 relative ${
              activeTab === 'clipboard'
                ? 'text-white bg-black/50'
                : 'text-gray-400 hover:text-white hover:bg-gray-700/50'
            }`}
          >
            <div className="flex items-center justify-center gap-2">
              <span>📋</span>
              <span>Clipboard</span>
              <span className="bg-gray-700 text-xs px-2 py-0.5 rounded-full">{copies.length}</span>
            </div>
            {activeTab === 'clipboard' && (
              <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-blue-500 to-purple-500 animate-[expandWidth_0.3s_ease-out]"></div>
            )}
          </button>
          <button
            onClick={() => handleTabChange('chat')}
            className={`flex-1 px-4 py-3 text-sm font-medium transition-all duration-300 relative ${
              activeTab === 'chat'
                ? 'text-white bg-black/50'
                : 'text-gray-400 hover:text-white hover:bg-gray-700/50'
            }`}
          >
            <div className="flex items-center justify-center gap-2">
              <span>🤖</span>
              <span>Assistant</span>
              {chatHistory.length > 0 && (
                <span className="bg-blue-600 text-xs px-2 py-0.5 rounded-full">{chatHistory.length}</span>
              )}
            </div>
            {activeTab === 'chat' && (
              <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-blue-500 to-purple-500 animate-[expandWidth_0.3s_ease-out]"></div>
            )}
          </button>
        </div>
      </div>

      {/* Tab Content */}
      <div className="relative z-10 h-[calc(100%-140px)]">
        {activeTab === 'clipboard' ? (
          <ClipboardPage 
            copies={copies} 
            setCopies={setCopies} 
            isDevMode={isDevMode}
          />
        ) : (
          <ChatPage 
            copies={copies} 
            chatHistory={chatHistory} 
            setChatHistory={setChatHistory}
            isDevMode={isDevMode}
          />
        )}
      </div>
    </div>
  );
}

export default App;
