import React, { useState } from 'react';

const ChatPage = ({ copies, chatHistory, setChatHistory }) => {
  const [chatMessage, setChatMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  const handleChat = async () => {
    if (!chatMessage.trim()) return;

    if (copies.length === 0) {
      const newHistory = [...chatHistory, { role: 'user', content: chatMessage }];
      setChatHistory([...newHistory, { 
        role: 'assistant', 
        content: 'Please copy some text first so I can help answer questions about it.',
        timestamp: new Date().toLocaleTimeString()
      }]);
      setChatMessage('');
      return;
    }

    const newHistory = [...chatHistory, { 
      role: 'user', 
      content: chatMessage,
      timestamp: new Date().toLocaleTimeString()
    }];
    setChatHistory(newHistory);
    setChatMessage('');
    setIsTyping(true);

    try {
      const response = await fetch('http://localhost:3001/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: chatMessage, copies })
      });
      const data = await response.json();
      
      // Simulate typing delay for better UX
      setTimeout(() => {
        setChatHistory([...newHistory, { 
          role: 'assistant', 
          content: data.response,
          timestamp: new Date().toLocaleTimeString()
        }]);
        setIsTyping(false);
      }, 1000);
    } catch (error) {
      console.error('Chat error:', error);
      setTimeout(() => {
        setChatHistory([...newHistory, { 
          role: 'assistant', 
          content: 'Unable to connect to chat service. Please check your connection.',
          timestamp: new Date().toLocaleTimeString()
        }]);
        setIsTyping(false);
      }, 500);
    }
  };

  const clearHistory = () => {
    setChatHistory([]);
  };

  return (
    <div className="h-full flex flex-col animate-[moonRise_0.8s_ease-out] relative overflow-hidden">
      {/* Moonlight overlay */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-900/30 via-indigo-900/20 to-purple-900/30 backdrop-blur-[1px] pointer-events-none"></div>
      
      {/* Floating particles */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="w-1 h-1 bg-blue-200/60 rounded-full absolute top-[20%] left-[15%] animate-[twinkle_2s_ease-in-out_infinite]"></div>
        <div className="w-1 h-1 bg-purple-200/60 rounded-full absolute top-[40%] right-[20%] animate-[twinkle_3s_ease-in-out_infinite]" style={{ animationDelay: '1s' }}></div>
        <div className="w-0.5 h-0.5 bg-indigo-200/60 rounded-full absolute top-[70%] left-[25%] animate-[twinkle_2.5s_ease-in-out_infinite]" style={{ animationDelay: '0.5s' }}></div>
      </div>

      <div className="p-4 flex-1 flex flex-col relative z-10">
        {/* Header with moon theme */}
        <div className="flex items-center justify-between mb-4 p-3 bg-gradient-to-r from-indigo-900/40 via-blue-900/30 to-purple-900/40 rounded-xl border border-blue-400/20 backdrop-blur-sm shadow-lg">
          <div className="flex items-center gap-3">
            <div className="relative">
              <div className="w-3 h-3 bg-gradient-to-r from-blue-400 to-cyan-300 rounded-full animate-pulse shadow-lg shadow-blue-400/50"></div>
              <div className="absolute inset-0 w-3 h-3 bg-gradient-to-r from-blue-400 to-cyan-300 rounded-full animate-ping opacity-75"></div>
            </div>
            <h2 className="text-sm font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-200 via-cyan-200 to-indigo-200 tracking-wide">
              Lunar Assistant
            </h2>
            <span className="text-xs text-blue-200/80 bg-gradient-to-r from-blue-900/60 to-indigo-900/60 px-3 py-1.5 rounded-full border border-blue-400/30 shadow-inner">
              {copies.length} stellar items
            </span>
          </div>
          {chatHistory.length > 0 && (
            <button 
              onClick={clearHistory}
              className="text-xs text-blue-300/70 hover:text-red-300 transition-all duration-300 hover:scale-110 transform bg-gradient-to-r from-red-900/30 to-pink-900/30 px-3 py-1.5 rounded-full border border-red-400/20 hover:border-red-400/40 hover:shadow-lg hover:shadow-red-400/20"
            >
              Clear History
            </button>
          )}
        </div>

        {/* Chat History with enhanced moon theme */}
        <div className="flex-1 bg-gradient-to-br from-slate-900/60 via-blue-900/40 to-indigo-900/60 backdrop-blur-md rounded-2xl border border-blue-400/20 p-5 mb-4 overflow-y-auto scroll-smooth shadow-2xl shadow-blue-900/20 relative">
          {/* Inner glow effect */}
          <div className="absolute inset-0 rounded-2xl bg-gradient-to-br from-blue-500/5 via-transparent to-purple-500/5 pointer-events-none"></div>
          
          {chatHistory.length === 0 ? (
            <div className="text-center py-12 animate-[fadeIn_0.8s_ease-out] relative z-10">
              <div className="w-20 h-20 bg-gradient-to-br from-blue-500/30 via-indigo-500/20 to-purple-500/30 rounded-3xl mx-auto mb-6 flex items-center justify-center animate-[moonFloat_4s_ease-in-out_infinite] border border-blue-400/30 shadow-2xl shadow-blue-500/20">
                <span className="text-3xl filter drop-shadow-lg">🌙</span>
              </div>
              <h3 className="text-base font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-200 via-cyan-200 to-indigo-200 mb-2 tracking-wide">
                Lunar AI Ready
              </h3>
              <p className="text-sm text-blue-200/70 mb-6 font-medium">
                Ask questions about your cosmic clipboard
              </p>
              <div className="space-y-3 text-sm text-blue-200/60">
                <div className="flex items-center justify-center gap-3 p-2 bg-gradient-to-r from-blue-900/30 to-indigo-900/30 rounded-lg border border-blue-400/20">
                  <span className="w-2 h-2 bg-gradient-to-r from-blue-400 to-cyan-300 rounded-full shadow-lg shadow-blue-400/50"></span>
                  <span className="font-medium">Summarize celestial content</span>
                </div>
                <div className="flex items-center justify-center gap-3 p-2 bg-gradient-to-r from-indigo-900/30 to-purple-900/30 rounded-lg border border-indigo-400/20">
                  <span className="w-2 h-2 bg-gradient-to-r from-indigo-400 to-purple-300 rounded-full shadow-lg shadow-indigo-400/50"></span>
                  <span className="font-medium">Extract stellar information</span>
                </div>
                <div className="flex items-center justify-center gap-3 p-2 bg-gradient-to-r from-purple-900/30 to-blue-900/30 rounded-lg border border-purple-400/20">
                  <span className="w-2 h-2 bg-gradient-to-r from-purple-400 to-blue-300 rounded-full shadow-lg shadow-purple-400/50"></span>
                  <span className="font-medium">Answer cosmic queries</span>
                </div>
              </div>
            </div>
          ) : (
            <div className="space-y-5 relative z-10">
              {chatHistory.map((msg, index) => (
                <div 
                  key={index} 
                  className={`animate-[slideInMessage_0.6s_ease-out] ${
                    msg.role === 'user' 
                      ? 'ml-8 text-right' 
                      : 'mr-8'
                  }`}
                  style={{ animationDelay: `${index * 0.1}s` }}
                >
                  <div className={`inline-block p-4 rounded-2xl text-sm max-w-[85%] break-words relative backdrop-blur-sm ${
                    msg.role === 'user'
                      ? 'bg-gradient-to-br from-blue-600/80 via-cyan-600/70 to-indigo-600/80 text-white shadow-2xl shadow-blue-500/20 border border-blue-400/30'
                      : 'bg-gradient-to-br from-slate-800/80 via-gray-800/70 to-indigo-800/80 border border-blue-400/20 text-blue-100 shadow-2xl shadow-indigo-500/10'
                  }`}>
                    <span className="relative z-10">{msg.content}</span>
                    <div className={`absolute w-4 h-4 backdrop-blur-sm ${
                      msg.role === 'user'
                        ? 'bg-gradient-to-br from-blue-600/80 to-indigo-600/80 -bottom-2 -right-2 rotate-45 border-r border-b border-blue-400/30'
                        : 'bg-gradient-to-br from-slate-800/80 to-indigo-800/80 -bottom-2 -left-2 rotate-45 border-l border-b border-blue-400/20'
                    }`}></div>
                  </div>
                  <div className={`text-xs text-blue-200/60 mt-3 ${
                    msg.role === 'user' ? 'text-right' : 'text-left'
                  }`}>
                    <span className="bg-gradient-to-r from-blue-900/60 to-indigo-900/60 px-3 py-1.5 rounded-full border border-blue-400/20 backdrop-blur-sm font-medium">
                      {msg.role === 'user' ? '🌟 You' : '🌙 Assistant'} • {msg.timestamp}
                    </span>
                  </div>
                </div>
              ))}
              
              {/* Enhanced typing indicator */}
              {isTyping && (
                <div className="mr-8 animate-[slideInMessage_0.6s_ease-out]">
                  <div className="inline-block p-4 rounded-2xl text-sm bg-gradient-to-br from-slate-800/80 via-gray-800/70 to-indigo-800/80 border border-blue-400/20 text-blue-100 shadow-2xl shadow-indigo-500/10 backdrop-blur-sm">
                    <div className="flex items-center gap-2">
                      <span className="text-xs text-blue-300/80 mr-2">🌙 thinking...</span>
                      <div className="flex gap-1">
                        <div className="w-2.5 h-2.5 bg-gradient-to-r from-blue-400 to-cyan-300 rounded-full animate-bounce shadow-lg shadow-blue-400/50"></div>
                        <div className="w-2.5 h-2.5 bg-gradient-to-r from-indigo-400 to-blue-300 rounded-full animate-bounce shadow-lg shadow-indigo-400/50" style={{ animationDelay: '0.1s' }}></div>
                        <div className="w-2.5 h-2.5 bg-gradient-to-r from-purple-400 to-indigo-300 rounded-full animate-bounce shadow-lg shadow-purple-400/50" style={{ animationDelay: '0.2s' }}></div>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Enhanced Chat Input */}
        <div className="flex gap-4 bg-gradient-to-r from-slate-900/70 via-blue-900/50 to-indigo-900/70 p-4 rounded-2xl border border-blue-400/20 backdrop-blur-md shadow-2xl shadow-blue-900/20">
          <div className="flex-1 relative">
            <input
              type="text"
              value={chatMessage}
              onChange={(e) => setChatMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleChat()}
              placeholder="Share your cosmic thoughts..."
              disabled={isTyping}
              className="w-full p-4 bg-black/40 border border-blue-400/30 rounded-xl text-sm text-blue-100 placeholder-blue-200/50 focus:outline-none focus:border-cyan-400/60 focus:ring-2 focus:ring-cyan-400/20 transition-all duration-300 disabled:opacity-50 backdrop-blur-sm shadow-inner font-medium"
            />
            {chatMessage && (
              <div className="absolute right-4 top-1/2 transform -translate-y-1/2">
                <span className="text-xs text-blue-300/70 bg-blue-900/40 px-2 py-1 rounded-full border border-blue-400/20">
                  {chatMessage.length}
                </span>
              </div>
            )}
          </div>
          <button 
            onClick={handleChat} 
            disabled={!chatMessage.trim() || isTyping}
            className="px-6 py-4 bg-gradient-to-r from-blue-600/80 via-cyan-600/70 to-indigo-600/80 hover:from-blue-500/90 hover:via-cyan-500/80 hover:to-indigo-500/90 disabled:from-gray-700/60 disabled:to-gray-600/60 disabled:cursor-not-allowed text-white rounded-xl text-sm font-bold transition-all duration-300 transform hover:scale-105 disabled:scale-100 shadow-2xl shadow-blue-500/20 border border-blue-400/30 backdrop-blur-sm hover:shadow-cyan-400/30"
          >
            {isTyping ? (
              <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
            ) : (
              <span className="flex items-center gap-2">
                Send <span className="text-lg">🚀</span>
              </span>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
