/* global chrome */
import React, { useState } from 'react';

const ClipboardPage = ({ copies, setCopies }) => {
  const [newCopyText, setNewCopyText] = useState('');
  const [copiedIndex, setCopiedIndex] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const handleCopyClick = async (copyText, index) => {
    try {
      await navigator.clipboard.writeText(copyText);
      setCopiedIndex(index);
      
      // Reset the copied indicator after 2 seconds
      setTimeout(() => setCopiedIndex(null), 2000);
    } catch (error) {
      console.error('Failed to copy text:', error);
    }
  };

  const handleAddCopy = () => {
    if (!newCopyText.trim()) return;

    const newCopies = [newCopyText, ...copies].slice(0, 10);
    setCopies(newCopies);
    setNewCopyText('');

    if (isDevMode) {
      localStorage.setItem('devCopies', JSON.stringify(newCopies));
    } else if (typeof chrome !== 'undefined' && chrome.storage) {
      chrome.storage.local.set({ copies: newCopies });
    }
  };

  const handleDeleteCopy = (indexToDelete) => {
    const newCopies = copies.filter((_, index) => index !== indexToDelete);
    setCopies(newCopies);

    if (isDevMode) {
      localStorage.setItem('devCopies', JSON.stringify(newCopies));
    } else if (typeof chrome !== 'undefined' && chrome.storage) {
      chrome.storage.local.set({ copies: newCopies });
    }
  };

  const clearClipboard = () => {
    setCopies([]);
    if (isDevMode) {
      localStorage.removeItem('devCopies');
    } else if (typeof chrome !== 'undefined' && chrome.storage) {
      chrome.storage.local.set({ copies: [] });
    }
  };

  // Filter copies based on search term
  const filteredCopies = copies.filter(copy => 
    copy.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatTimeAgo = (index) => {
    const minutesAgo = index * 2; // Simulate time difference
    if (minutesAgo === 0) return 'Just now';
    if (minutesAgo < 60) return `${minutesAgo}m ago`;
    const hoursAgo = Math.floor(minutesAgo / 60);
    return `${hoursAgo}h ago`;
  };

  return (
    <div className="h-full flex flex-col animate-[fadeInUp_0.4s_ease-out]">
      <div className="p-6 flex-1 overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="w-3 h-3 bg-gradient-to-r from-green-400 to-blue-400 rounded-full animate-pulse shadow-lg"></div>
            <h2 className="text-lg font-bold text-white">Clipboard Manager</h2>
            <span className="text-sm text-blue-200/80 bg-white/10 px-3 py-1.5 rounded-full backdrop-blur-sm border border-white/20">
              {copies.length}/10 items
            </span>
          </div>
          {copies.length > 0 && (
            <button 
              onClick={clearClipboard}
              className="text-sm text-red-300/80 hover:text-red-200 transition-all duration-300 hover:scale-105 bg-red-500/10 hover:bg-red-500/20 px-3 py-1.5 rounded-lg backdrop-blur-sm border border-red-500/20 hover:border-red-500/40"
            >
              Clear All
            </button>
          )}
        </div>

        {/* Search Bar */}
        {copies.length > 0 && (
          <div className="mb-6">
            <div className="relative">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search your clipboard..."
                className="w-full p-4 bg-white/10 backdrop-blur-lg border border-white/20 rounded-xl text-white placeholder-blue-200/60 focus:outline-none focus:border-blue-400/60 focus:ring-2 focus:ring-blue-400/20 transition-all duration-300 shadow-lg"
              />
              <div className="absolute right-4 top-1/2 transform -translate-y-1/2">
                <span className="text-blue-200/60 text-lg">🔍</span>
              </div>
            </div>
          </div>
        )}
        
        {/* Clipboard Items */}
        <div className="h-full overflow-y-auto space-y-4 pb-6 scrollbar-thin scrollbar-thumb-white/20 scrollbar-track-transparent">
          {filteredCopies.length === 0 ? (
            <div className="text-center py-16 text-blue-200/80 animate-[fadeIn_0.6s_ease-out]">
              {copies.length === 0 ? (
                <div className="relative">
                  <div className="w-24 h-24 bg-gradient-to-br from-blue-500/20 to-purple-500/20 rounded-3xl mx-auto mb-6 flex items-center justify-center animate-[float_4s_ease-in-out_infinite] backdrop-blur-lg border border-white/10 shadow-2xl">
                    <span className="text-4xl">📋</span>
                  </div>
                  <h3 className="text-xl font-bold text-white mb-2">No items yet</h3>
                  <p className="text-blue-200/60 mb-6">Copy some text to get started</p>
                  <div className="space-y-3 text-sm text-blue-200/50">
                    <div className="flex items-center justify-center gap-3">
                      <kbd className="px-3 py-1.5 bg-white/10 rounded-lg border border-white/20 font-mono text-xs backdrop-blur-sm">Ctrl+C</kbd>
                      <span>to copy text</span>
                    </div>
                    <div className="flex items-center justify-center gap-3">
                      <span className="w-2 h-2 bg-blue-400 rounded-full"></span>
                      <span>Up to 10 recent items stored</span>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="relative">
                  <div className="w-20 h-20 bg-gradient-to-br from-yellow-500/20 to-orange-500/20 rounded-2xl mx-auto mb-4 flex items-center justify-center backdrop-blur-lg border border-white/10">
                    <span className="text-3xl">🔍</span>
                  </div>
                  <h3 className="text-lg font-semibold text-white mb-1">No results found</h3>
                  <p className="text-blue-200/60">Try a different search term</p>
                </div>
              )}
            </div>
          ) : (
            filteredCopies.map((copy, index) => (
              <div 
                key={index} 
                className="group bg-gradient-to-r from-white/10 to-white/5 hover:from-white/15 hover:to-white/10 backdrop-blur-lg p-5 rounded-2xl border border-white/20 hover:border-white/40 cursor-pointer transition-all duration-500 transform hover:scale-[1.02] hover:shadow-2xl animate-[slideInUp_0.5s_ease-out] shadow-lg"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-start justify-between gap-4">
                  <div 
                    className="flex-1 min-w-0"
                    onClick={() => handleCopyClick(copy, index)}
                  >
                    <div className="text-white/90 break-words mb-3 leading-relaxed font-medium" title={copy}>
                      {copy.length > 120 ? `${copy.substring(0, 120)}...` : copy}
                    </div>
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3 text-xs text-blue-200/70">
                        <span className="bg-white/10 px-3 py-1.5 rounded-full backdrop-blur-sm border border-white/20 font-semibold">
                          #{copies.length - index}
                        </span>
                        <span className="w-1 h-1 bg-blue-300 rounded-full"></span>
                        <span>{formatTimeAgo(index)}</span>
                        <span className="w-1 h-1 bg-blue-300 rounded-full"></span>
                        <span>{copy.length} chars</span>
                      </div>
                      <div className="flex items-center gap-2">
                        {copiedIndex === index ? (
                          <span className="text-xs text-green-300 flex items-center gap-2 animate-[fadeIn_0.4s_ease-out] bg-green-500/20 px-3 py-1.5 rounded-full backdrop-blur-sm border border-green-400/30">
                            <span className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></span>
                            Copied!
                          </span>
                        ) : (
                          <span className="text-xs text-blue-200/50 opacity-0 group-hover:opacity-100 transition-all duration-300 bg-white/10 px-3 py-1.5 rounded-full backdrop-blur-sm">
                            Click to copy
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                  
                  <div className="flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-all duration-300">
                    <button
                      onClick={() => handleCopyClick(copy, index)}
                      className="p-3 text-blue-200/70 hover:text-blue-200 hover:bg-blue-500/20 rounded-xl transition-all duration-300 transform hover:scale-110 backdrop-blur-sm border border-white/10 hover:border-blue-400/30"
                      title="Copy to clipboard"
                    >
                      📋
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteCopy(index);
                      }}
                      className="p-3 text-red-300/70 hover:text-red-200 hover:bg-red-500/20 rounded-xl transition-all duration-300 transform hover:scale-110 backdrop-blur-sm border border-white/10 hover:border-red-400/30"
                      title="Delete item"
                    >
                      🗑️
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Development Mode */}
      {isDevMode && (
        <div className="p-6 bg-gradient-to-r from-yellow-500/20 to-orange-500/20 border-t border-yellow-400/30 backdrop-blur-lg">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-3 h-3 bg-yellow-400 rounded-full animate-pulse shadow-lg"></div>
            <div className="text-sm text-yellow-200 font-bold">Development Mode</div>
          </div>
          <div className="flex gap-3">
            <input
              type="text"
              value={newCopyText}
              onChange={(e) => setNewCopyText(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleAddCopy()}
              placeholder="Add test item..."
              className="flex-1 p-4 bg-black/30 backdrop-blur-lg border border-yellow-400/30 rounded-xl text-white placeholder-yellow-200/60 focus:outline-none focus:border-yellow-400/60 focus:ring-2 focus:ring-yellow-400/20 transition-all duration-300 shadow-lg"
            />
            <button 
              onClick={handleAddCopy} 
              disabled={!newCopyText.trim()}
              className="px-6 py-4 bg-gradient-to-r from-yellow-500 to-orange-500 hover:from-yellow-400 hover:to-orange-400 disabled:from-gray-600 disabled:to-gray-500 disabled:cursor-not-allowed text-white rounded-xl font-semibold transition-all duration-300 transform hover:scale-105 disabled:scale-100 shadow-lg backdrop-blur-sm border border-yellow-400/30"
            >
              Add
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ClipboardPage;
