/* global chrome */
import React, { useState } from 'react';

const ClipboardPage = ({ copies, setCopies, isDevMode }) => {
  const [newCopyText, setNewCopyText] = useState('');
  const [copiedIndex, setCopiedIndex] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const handleCopyClick = async (copyText, index) => {
    try {
      await navigator.clipboard.writeText(copyText);
      setCopiedIndex(index);
      
      // Reset the copied indicator after 2 seconds
      setTimeout(() => setCopiedIndex(null), 2000);
    } catch (error) {
      console.error('Failed to copy text:', error);
    }
  };

  const handleAddCopy = () => {
    if (!newCopyText.trim()) return;

    const newCopies = [newCopyText, ...copies].slice(0, 10);
    setCopies(newCopies);
    setNewCopyText('');

    if (isDevMode) {
      localStorage.setItem('devCopies', JSON.stringify(newCopies));
    } else if (typeof chrome !== 'undefined' && chrome.storage) {
      chrome.storage.local.set({ copies: newCopies });
    }
  };

  const handleDeleteCopy = (indexToDelete) => {
    const newCopies = copies.filter((_, index) => index !== indexToDelete);
    setCopies(newCopies);

    if (isDevMode) {
      localStorage.setItem('devCopies', JSON.stringify(newCopies));
    } else if (typeof chrome !== 'undefined' && chrome.storage) {
      chrome.storage.local.set({ copies: newCopies });
    }
  };

  const clearClipboard = () => {
    setCopies([]);
    if (isDevMode) {
      localStorage.removeItem('devCopies');
    } else if (typeof chrome !== 'undefined' && chrome.storage) {
      chrome.storage.local.set({ copies: [] });
    }
  };

  // Filter copies based on search term
  const filteredCopies = copies.filter(copy => 
    copy.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatTimeAgo = (index) => {
    const minutesAgo = index * 2; // Simulate time difference
    if (minutesAgo === 0) return 'Just now';
    if (minutesAgo < 60) return `${minutesAgo}m ago`;
    const hoursAgo = Math.floor(minutesAgo / 60);
    return `${hoursAgo}h ago`;
  };

  return (
    <div className="h-full flex flex-col animate-[fadeInUp_0.3s_ease-out]">
      <div className="p-4 flex-1 overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
            <h2 className="text-sm font-medium text-gray-300">Clipboard Manager</h2>
            <span className="text-xs text-gray-500 bg-gray-800 px-2 py-1 rounded-full">
              {copies.length}/10
            </span>
          </div>
          {copies.length > 0 && (
            <button 
              onClick={clearClipboard}
              className="text-xs text-gray-400 hover:text-red-400 transition-colors hover:scale-105 transform"
            >
              Clear All
            </button>
          )}
        </div>

        {/* Search Bar */}
        {copies.length > 0 && (
          <div className="mb-4">
            <div className="relative">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search clipboard..."
                className="w-full p-3 bg-gray-900/50 border border-gray-700 rounded-lg text-sm text-white placeholder-gray-500 focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all"
              />
              <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                <span className="text-gray-400">🔍</span>
              </div>
            </div>
          </div>
        )}
        
        {/* Clipboard Items */}
        <div className="h-full overflow-y-auto space-y-3 pb-4">
          {filteredCopies.length === 0 ? (
            <div className="text-center py-12 text-gray-500 animate-[fadeIn_0.5s_ease-out]">
              {copies.length === 0 ? (
                <>
                  <div className="w-20 h-20 bg-gradient-to-br from-gray-800 to-gray-700 rounded-2xl mx-auto mb-4 flex items-center justify-center animate-[float_3s_ease-in-out_infinite]">
                    <span className="text-3xl">📋</span>
                  </div>
                  <p className="text-sm font-medium mb-1">No copied items yet</p>
                  <p className="text-xs text-gray-600">Copy some text to get started</p>
                  <div className="mt-4 text-xs text-gray-600">
                    <div className="flex items-center justify-center gap-2 mb-2">
                      <kbd className="px-2 py-1 bg-gray-800 rounded text-xs">Ctrl+C</kbd>
                      <span>to copy text</span>
                    </div>
                    <div className="flex items-center justify-center gap-2">
                      <span>Up to 10 recent items</span>
                    </div>
                  </div>
                </>
              ) : (
                <>
                  <div className="w-16 h-16 bg-gray-800 rounded-lg mx-auto mb-4 flex items-center justify-center">
                    <span className="text-2xl">🔍</span>
                  </div>
                  <p className="text-sm">No results found</p>
                  <p className="text-xs text-gray-600 mt-1">Try a different search term</p>
                </>
              )}
            </div>
          ) : (
            filteredCopies.map((copy, index) => (
              <div 
                key={index} 
                className="group bg-gradient-to-r from-gray-900/80 to-gray-800/80 hover:from-gray-800/80 hover:to-gray-700/80 backdrop-blur-sm p-4 rounded-xl border border-gray-700/50 hover:border-gray-600/70 cursor-pointer transition-all duration-300 transform hover:scale-[1.02] hover:shadow-xl animate-[slideInUp_0.4s_ease-out]"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-start justify-between gap-3">
                  <div 
                    className="flex-1 min-w-0"
                    onClick={() => handleCopyClick(copy, index)}
                  >
                    <div className="text-sm text-gray-200 break-words mb-2 leading-relaxed" title={copy}>
                      {copy.length > 120 ? `${copy.substring(0, 120)}...` : copy}
                    </div>
                    <div className="flex items-center justify-between">
                      <div className="text-xs text-gray-500 flex items-center gap-2">
                        <span className="bg-gray-800/50 px-2 py-1 rounded-full">
                          Item #{copies.length - index}
                        </span>
                        <span>•</span>
                        <span>{formatTimeAgo(index)}</span>
                        <span>•</span>
                        <span>{copy.length} chars</span>
                      </div>
                      <div className="flex items-center gap-2">
                        {copiedIndex === index ? (
                          <span className="text-xs text-green-400 flex items-center gap-1 animate-[fadeIn_0.3s_ease-out]">
                            <span className="w-3 h-3 bg-green-400 rounded-full"></span>
                            Copied!
                          </span>
                        ) : (
                          <span className="text-xs text-gray-400 opacity-0 group-hover:opacity-100 transition-opacity">
                            Click to copy
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                  
                  <div className="flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                    <button
                      onClick={() => handleCopyClick(copy, index)}
                      className="p-2 text-gray-400 hover:text-blue-400 hover:bg-blue-500/10 rounded-lg transition-all transform hover:scale-110"
                      title="Copy to clipboard"
                    >
                      📋
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteCopy(index);
                      }}
                      className="p-2 text-gray-400 hover:text-red-400 hover:bg-red-500/10 rounded-lg transition-all transform hover:scale-110"
                      title="Delete item"
                    >
                      🗑️
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Development Mode */}
      {isDevMode && (
        <div className="p-4 bg-gradient-to-r from-yellow-900/20 to-orange-900/20 border-t border-yellow-600/30 backdrop-blur-sm">
          <div className="flex items-center gap-2 mb-3">
            <div className="w-2 h-2 bg-yellow-400 rounded-full animate-pulse"></div>
            <div className="text-xs text-yellow-400 font-medium">Development Mode</div>
          </div>
          <div className="flex gap-2">
            <input
              type="text"
              value={newCopyText}
              onChange={(e) => setNewCopyText(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleAddCopy()}
              placeholder="Add test item..."
              className="flex-1 p-3 bg-black/50 border border-gray-600 rounded-lg text-sm text-white placeholder-gray-500 focus:outline-none focus:border-yellow-500 focus:ring-2 focus:ring-yellow-500/20 transition-all"
            />
            <button 
              onClick={handleAddCopy} 
              disabled={!newCopyText.trim()}
              className="px-6 py-3 bg-gradient-to-r from-yellow-600 to-orange-600 hover:from-yellow-500 hover:to-orange-500 disabled:from-gray-700 disabled:to-gray-600 disabled:cursor-not-allowed text-white rounded-lg text-sm font-medium transition-all transform hover:scale-105 disabled:scale-100"
            >
              Add
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ClipboardPage;
