/* global chrome */
import React, { useState, useEffect } from 'react';

function App() {
  const [copies, setCopies] = useState([]);
  const [chatMessage, setChatMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [newCopyText, setNewCopyText] = useState('');
  const [isDevMode] = useState(typeof chrome === 'undefined' || !chrome.storage);
  const [typewriterText, setTypewriterText] = useState('');
  const [isTyping, setIsTyping] = useState(true);
  const [activeTab, setActiveTab] = useState('clipboard');
  
  const fullText = "CopyCache";

  // Typewriter effect for logo
  useEffect(() => {
    if (isTyping && typewriterText.length < fullText.length) {
      const timeout = setTimeout(() => {
        setTypewriterText(fullText.slice(0, typewriterText.length + 1));
      }, 120);
      return () => clearTimeout(timeout);
    } else if (typewriterText.length === fullText.length) {
      const timeout = setTimeout(() => setIsTyping(false), 500);
      return () => clearTimeout(timeout);
    }
  }, [typewriterText, isTyping, fullText]);

  // Load copies from storage
  useEffect(() => {
    if (typeof chrome !== 'undefined' && chrome.storage && chrome.storage.local) {
      chrome.storage.local.get(['copies'], (result) => {
        if (result.copies) {
          setCopies(result.copies);
        }
      });

      // Listen for new copies in real-time
      chrome.storage.onChanged.addListener((changes, namespace) => {
        if (namespace === 'local' && changes.copies) {
          setCopies(changes.changes.newValue || []);
        }
      });
    } else {
      // Development mode fallback
      const devCopies = localStorage.getItem('devCopies');
      if (devCopies) {
        setCopies(JSON.parse(devCopies));
      }
    }
  }, []);

  const handleAddCopy = () => {
    if (!newCopyText.trim()) return;

    const newCopies = [newCopyText, ...copies].slice(0, 10);
    setCopies(newCopies);
    setNewCopyText('');

    if (isDevMode) {
      localStorage.setItem('devCopies', JSON.stringify(newCopies));
    } else if (chrome && chrome.storage) {
      chrome.storage.local.set({ copies: newCopies });
    }
  };

  const handleChat = async () => {
    if (!chatMessage.trim()) return;

    if (copies.length === 0) {
      const newHistory = [...chatHistory, { role: 'user', content: chatMessage }];
      setChatHistory([...newHistory, { 
        role: 'assistant', 
        content: 'Please copy some text first so I can help answer questions about it.' 
      }]);
      setChatMessage('');
      return;
    }

    const newHistory = [...chatHistory, { role: 'user', content: chatMessage }];
    setChatHistory(newHistory);
    setChatMessage('');

    try {
      // Real implementation would connect to your backend
      const response = await fetch('http://localhost:3001/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: chatMessage, copies })
      });
      const data = await response.json();
      setChatHistory([...newHistory, { role: 'assistant', content: data.response }]);
    } catch (error) {
      console.error('Chat error:', error);
      setChatHistory([...newHistory, { 
        role: 'assistant', 
        content: 'Unable to connect to chat service. Please check your connection.' 
      }]);
    }
  };

  const handleCopyClick = (copyText) => {
    navigator.clipboard.writeText(copyText);
  };

  const clearHistory = () => {
    setChatHistory([]);
  };

  const clearClipboard = () => {
    setCopies([]);
    if (isDevMode) {
      localStorage.removeItem('devCopies');
    } else if (chrome && chrome.storage) {
      chrome.storage.local.set({ copies: [] });
    }
  };

  return (
    <div className="w-[400px] h-[600px] bg-black text-white overflow-hidden">
      
      {/* Header */}
      <div className="bg-gray-900 border-b border-gray-700 p-4">
        <div className="flex items-center gap-3 mb-2">
          <div className="w-8 h-8 bg-gray-700 rounded flex items-center justify-center">
            <span className="text-white font-mono text-sm">CC</span>
          </div>
          <h1 className="text-xl font-semibold text-white">
            <span className="font-mono">
              {typewriterText}
              {isTyping && <span className="animate-pulse">|</span>}
            </span>
          </h1>
        </div>
        <p className="text-gray-400 text-sm">Clipboard Manager</p>
      </div>

      {/* Navigation Tabs */}
      <div className="bg-gray-800 border-b border-gray-700">
        <div className="flex">
          <button
            onClick={() => setActiveTab('clipboard')}
            className={`flex-1 px-4 py-3 text-sm font-medium transition-colors ${
              activeTab === 'clipboard'
                ? 'bg-black text-white border-b-2 border-blue-500'
                : 'text-gray-400 hover:text-white hover:bg-gray-700'
            }`}
          >
            Clipboard ({copies.length})
          </button>
          <button
            onClick={() => setActiveTab('chat')}
            className={`flex-1 px-4 py-3 text-sm font-medium transition-colors ${
              activeTab === 'chat'
                ? 'bg-black text-white border-b-2 border-blue-500'
                : 'text-gray-400 hover:text-white hover:bg-gray-700'
            }`}
          >
            Assistant
          </button>
        </div>
      </div>

      {/* Tab Content */}
      <div className="flex-1 h-[calc(100%-140px)]">
        
        {/* Clipboard Tab */}
        {activeTab === 'clipboard' && (
          <div className="h-full flex flex-col">
            <div className="p-4 flex-1 overflow-hidden">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-sm font-medium text-gray-300">Recent Items</h2>
                {copies.length > 0 && (
                  <button 
                    onClick={clearClipboard}
                    className="text-xs text-gray-400 hover:text-red-400 transition-colors"
                  >
                    Clear All
                  </button>
                )}
              </div>
              
              <div className="h-full overflow-y-auto space-y-2">
                {copies.length === 0 ? (
                  <div className="text-center py-12 text-gray-500">
                    <div className="w-16 h-16 bg-gray-800 rounded-lg mx-auto mb-4 flex items-center justify-center">
                      <span className="text-2xl text-gray-600">â–¡</span>
                    </div>
                    <p className="text-sm">No copied items yet</p>
                    <p className="text-xs text-gray-600 mt-1">Copy some text to get started</p>
                  </div>
                ) : (
                  copies.map((copy, index) => (
                    <div 
                      key={index} 
                      onClick={() => handleCopyClick(copy)}
                      className="group bg-gray-900 hover:bg-gray-800 p-4 rounded-lg border border-gray-700 hover:border-gray-600 cursor-pointer transition-all"
                    >
                      <div className="text-sm text-gray-200 break-words mb-2" title={copy}>
                        {copy.length > 100 ? `${copy.substring(0, 100)}...` : copy}
                      </div>
                      <div className="text-xs text-gray-500 flex justify-between items-center">
                        <span>Item {copies.length - index}</span>
                        <span className="opacity-0 group-hover:opacity-100 transition-opacity text-gray-400">
                          Click to copy
                        </span>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>

            {isDevMode && (
              <div className="p-4 bg-gray-900 border-t border-gray-700">
                <div className="text-xs text-yellow-400 mb-2 font-medium">Development Mode</div>
                <div className="flex gap-2">
                  <input
                    type="text"
                    value={newCopyText}
                    onChange={(e) => setNewCopyText(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleAddCopy()}
                    placeholder="Add test item..."
                    className="flex-1 p-2 bg-black border border-gray-600 rounded text-sm text-white placeholder-gray-500 focus:outline-none focus:border-blue-500"
                  />
                  <button 
                    onClick={handleAddCopy} 
                    className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded text-sm font-medium transition-colors"
                  >
                    Add
                  </button>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Chat Tab */}
        {activeTab === 'chat' && (
          <div className="h-full flex flex-col">
            <div className="p-4 flex-1 flex flex-col">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-sm font-medium text-gray-300">AI Assistant</h2>
                {chatHistory.length > 0 && (
                  <button 
                    onClick={clearHistory}
                    className="text-xs text-gray-400 hover:text-red-400 transition-colors"
                  >
                    Clear History
                  </button>
                )}
              </div>

              {/* Chat History */}
              <div className="flex-1 bg-gray-900 rounded-lg border border-gray-700 p-4 mb-4 overflow-y-auto">
                {chatHistory.length === 0 ? (
                  <div className="text-center text-gray-500 py-8">
                    <div className="w-12 h-12 bg-gray-800 rounded-lg mx-auto mb-3 flex items-center justify-center">
                      <span className="text-xl text-gray-600">?</span>
                    </div>
                    <p className="text-sm">Ask questions about your copied items</p>
                  </div>
                ) : (
                  <div className="space-y-3">
                    {chatHistory.map((msg, index) => (
                      <div 
                        key={index} 
                        className={`${
                          msg.role === 'user' 
                            ? 'ml-6 text-right' 
                            : 'mr-6'
                        }`}
                      >
                        <div className={`inline-block p-3 rounded-lg text-sm max-w-[85%] break-words ${
                          msg.role === 'user'
                            ? 'bg-blue-600 text-white'
                            : 'bg-gray-800 border border-gray-600 text-gray-200'
                        }`}>
                          {msg.content}
                        </div>
                        <div className={`text-xs text-gray-500 mt-1 ${
                          msg.role === 'user' ? 'text-right' : 'text-left'
                        }`}>
                          {msg.role === 'user' ? 'You' : 'Assistant'}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Chat Input */}
              <div className="flex gap-2">
                <input
                  type="text"
                  value={chatMessage}
                  onChange={(e) => setChatMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleChat()}
                  placeholder="Ask about your clipboard..."
                  className="flex-1 p-3 bg-black border border-gray-600 rounded-lg text-sm text-white placeholder-gray-500 focus:outline-none focus:border-blue-500"
                />
                <button 
                  onClick={handleChat} 
                  disabled={!chatMessage.trim()}
                  className="px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-700 disabled:cursor-not-allowed text-white rounded-lg text-sm font-medium transition-colors"
                >
                  Send
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
